-- ============================================================================
-- UK LOCATIONS IMPORT SCRIPT
-- Generated from CSV: 564 places, 217 admin areas
-- Run this AFTER v4 migration and v4.1 patch
-- ============================================================================

-- STEP 1: Insert Admin Areas (217 total)
ON CONFLICT (slug) DO NOTHING;

-- STEP 2: Insert Places (564 total)
ON CONFLICT (slug) DO NOTHING;

-- STEP 3: Update Counts

UPDATE countries SET 
  admin_area_count = (SELECT COUNT(*) FROM admin_areas WHERE country_id = countries.id),
  place_count = (SELECT COUNT(*) FROM places WHERE country_id = countries.id);

UPDATE admin_areas SET
  place_count = (SELECT COUNT(*) FROM places WHERE admin_area_id = admin_areas.id);

-- STEP 4: Verify Import
SELECT c.name as country, c.place_count, c.admin_area_count 
FROM countries c 
ORDER BY c.display_order;

SELECT COUNT(*) as total_places FROM places;
SELECT COUNT(*) as total_admin_areas FROM admin_areas;
